    private void btnConfirmActionPerformed(java.awt.event.ActionEvent evt)//GEN-FIRST:event_btnConfirmActionPerformed
    {//GEN-HEADEREND:event_btnConfirmActionPerformed
        if (this.cbDBMS.getSelectedIndex() < 2 && !this.getUserName().isBlank() && !this.getPassword().isBlank())
        {
            this.confirm = true;
            dispose();
        }
        else if (this.cbDBMS.getSelectedIndex() == 2)
        {
            this.confirm = true;
            dispose();
        }
    }//GEN-LAST:event_btnConfirmActionPerformed

    private void btnCancelActionPerformed(java.awt.event.ActionEvent evt)//GEN-FIRST:event_btnCancelActionPerformed
    {//GEN-HEADEREND:event_btnCancelActionPerformed
        dispose();
    }//GEN-LAST:event_btnCancelActionPerformed

    private void cbDBMSActionPerformed(java.awt.event.ActionEvent evt)//GEN-FIRST:event_cbDBMSActionPerformed
    {//GEN-HEADEREND:event_cbDBMSActionPerformed
        if (this.cbDBMS.getSelectedIndex() == 2) // SQLite
        {
            this.tfUserName.setText("");
            this.tfPassword.setText("");
            this.tfUserName.setEditable(false);
            this.tfPassword.setEditable(false);
        }
        else
        {
            this.tfUserName.setEditable(true);
            this.tfPassword.setEditable(true);
        }
    }//GEN-LAST:event_cbDBMSActionPerformed

